-- Create the UserRole enum
CREATE TYPE "UserRole" AS ENUM ('student', 'organizer', 'admin');

-- Create the users table
CREATE TABLE "users" (
    "id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "name" VARCHAR(100) NOT NULL,
    "email" VARCHAR(255) UNIQUE NOT NULL,
    "passwordHash" VARCHAR(255) NOT NULL,
    "role" "UserRole" NOT NULL,
    "createdAt" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP NOT NULL,
    "updatedAt" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP NOT NULL
);

-- Create the user_profiles table
CREATE TABLE "user_profiles" (
    "id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "user_id" BIGINT UNIQUE NOT NULL,
    "bio" VARCHAR(500),
    "createdAt" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP NOT NULL,
    "updatedAt" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP NOT NULL,
    CONSTRAINT "user_profiles_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "users"("id") ON DELETE RESTRICT ON UPDATE CASCADE
);

-- Create the events table
CREATE TABLE "events" (
    "id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "title" VARCHAR(255) NOT NULL,
    "description" TEXT,
    "date" TIMESTAMP(6),
    "user_id" BIGINT NOT NULL,
    "createdAt" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP NOT NULL,
    "updatedAt" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP NOT NULL,
    CONSTRAINT "events_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "users"("id") ON DELETE RESTRICT ON UPDATE CASCADE
);

-- Create the event_attendees table (junction table)
CREATE TABLE "event_attendees" (
    "id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "user_id" BIGINT NOT NULL,
    "event_id" BIGINT NOT NULL,
    "status" VARCHAR(50),
    CONSTRAINT "event_attendees_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "users"("id") ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT "event_attendees_event_id_fkey" FOREIGN KEY ("event_id") REFERENCES "events"("id") ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT "event_attendees_user_id_event_id_key" UNIQUE ("user_id", "event_id")
);

-- Add relations to users table (for Prisma compatibility)
-- Note: Prisma handles these automatically; included for clarity
ALTER TABLE "users" ADD CONSTRAINT "users_profile_fkey" FOREIGN KEY ("id") REFERENCES "user_profiles"("user_id") ON DELETE SET NULL ON UPDATE CASCADE;
-- Note: One-to-many for events is handled via foreign key in events table
-- Note: Many-to-many for attendedEvents is handled via event_attendees table

-- Indexes for performance
CREATE INDEX "users_email_idx" ON "users"("email");
CREATE INDEX "user_profiles_user_id_idx" ON "user_profiles"("user_id");
CREATE INDEX "events_user_id_idx" ON "events"("user_id");
CREATE INDEX "event_attendees_user_id_idx" ON "event_attendees"("user_id");
CREATE INDEX "event_attendees_event_id_idx" ON "event_attendees"("event_id");

-- Trigger for updatedAt on users
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW."updatedAt" = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER "users_updated_at" BEFORE UPDATE ON "users" FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

-- Trigger for updatedAt on user_profiles
CREATE TRIGGER "user_profiles_updated_at" BEFORE UPDATE ON "user_profiles" FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

-- Trigger for updatedAt on events
CREATE TRIGGER "events_updated_at" BEFORE UPDATE ON "events" FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();
